import React, {useEffect} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import CircularProgress from '@material-ui/core/CircularProgress';
import { useSelector } from 'react-redux';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 240,
    maxWidth: 450,
    width: '100%', 
    // margin: 0,
    fullWidth: true,
    // display: "flex",
    // wrap: "nowrap"

  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

export default function FormSelect(props) {
  const classes = useStyles();

  const waterbasesMain = useSelector(state => state.waterBases.waterBasesList)
  const waterbases = useSelector(state => state.waterBases.wBfilterList)
  const rigions = useSelector(state => state.rigions.rigionList)

  useEffect(() => {
    if (waterbases.length) {
      props.handleSelectChange(waterbases[0].name);
    }
  }, [waterbases]);

  const handleChange = (event) => {

    props.handleSelectChange(event.target.value);

  };

  return (
    <div> {
      waterbasesMain.length && waterbases.length ?
        <FormControl className={classes.formControl}>
           <InputLabel htmlFor="age-native-simple">Выберите водобазу</InputLabel>
                <Select
                native
                onChange={handleChange}
                label={props.name}

                inputProps={{
                    name: props.name,
                    id: 'age-native-simple',
                }}
                >
                { waterbases.map(item => <option key={item.uuid } {...item} value={item.name}>{item.name}</option>) }
                </Select>
      </FormControl>
        : 
        <CircularProgress />
        }


    </div>
  );

    if (waterbasesMain.length && !waterbases.length) {
            return (
            <div>
              <p>В вашем регионе нет водобаз. Пожалуйста кликните на карт</p>
              <p>Пожалуйста кликните на карте на регион из списка ниже, где есть водобазы</p>
              <div>
              { rigions.map(item => <p key={item.uuid } {...item} >{item.area_names[0]} {item.area_names[1]}</p>) }
              </div>
            </div>

           )
        }

}
