import React, { useState, useEffect, useRef } from 'react';
import FormSelect from './delivery_form-select'
import FormUpload from './delivery_form-upload'
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import InputAdornment from '@material-ui/core/InputAdornment';
import blue from "@material-ui/core/colors/blue";
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import NumberFormat from 'react-number-format';
import PropTypes from 'prop-types';
import DateFnsUtils from '@date-io/date-fns';
import CircularProgress from '@material-ui/core/CircularProgress';


import {
    MuiPickersUtilsProvider,
    KeyboardDatePicker,
  } from '@material-ui/pickers';

function NumberFormatCustom(props) {
    const { inputRef, onChange, ...other } = props;

    return (
      <NumberFormat
        {...other}
        getInputRef={inputRef}
        onValueChange={values => {
          onChange({
            target: {
              value: values.value,
            },
          });
        }}
        isNumericString
      />
    );
  }

  NumberFormatCustom.propTypes = {
    inputRef: PropTypes.func.isRequired,
    onChange: PropTypes.func.isRequired,
  };

const useStyles = makeStyles((theme) => ({
    card: {
      minWidth: 275,
    },
    button: {
      color: blue[900],
      margin: 10
    },
    root: {
      '& .MuiTextField-root': {
        margin: theme.spacing(1),
        minWidth: 240,
        maxWidth: 450,
        width: '100%',
        textField: {
            flexBasis: 200,
            textField: {
                marginLeft: theme.spacing(1),
                marginRight: theme.spacing(1),
              },
          },
      },
    },
  }));



function DeliveryForm(props) {
  const form = useRef();

    const [formState, setFormState] = React.useState({
      data: {
        rigion: null,
        waterbase: null,
        weight: null,
        adress: null,
        date: new Date(),
        upload: null
      }
    });

      // const [formErros, setFormErros] = useState({
      //   erros: {
      //     rigion: '',
      //     waterbase: '',
      //     weight: '',
      //     adress: '',
      //     date: new Date(),
      //     upload: ''
      //   }
      // })

      useEffect(()=> {
        formState.rigion = props.rigionValue
      }, [])


      const handleChange = prop => event => {
        setFormState({
             ...formState,
              [prop]: event.target.value 
            });
      };

      const handleSelectChange = value => {
        setFormState({
             ...formState,
              waterbase: value
            });
      };

      const handleDateChange = prop => {

        setFormState({
            ...formState,
             date: prop
           });
    };

    const handleUploadFile = prop => {
        setFormState({
            ...formState,
             upload: [prop]
           });
    };

    const submitForm = e => {
      e.preventDefault();

    }

    const error = formState.adress !== "a";

    const classes = useStyles();

    return (
      <Card className={classes.card}>
        <CardContent>
        <div className="form-wrapper">
        <form className={classes.root}  autoComplete="off" onSubmit={submitForm} ref={form} debounceTime={1500}>
             {props.rigionValue ? <TextField 
                required 
                id="standard-required1" 
                label="Ваш регион"
                className={classes.textField}
                value={props.rigionValue}
                InputProps={{
                    readOnly: true,
                }}
             />
             : <CircularProgress />
            }
            <FormSelect handleSelectChange={handleSelectChange} name={formState.waterbase}/>
            <TextField 
                required 
                id="standard-required2" 
                label="Напишите массу воды в тоннах"
                className={classes.textField}
                value={formState.weight}
                onChange={handleChange('weight')}
                InputProps={{
                    inputComponent: NumberFormatCustom,
                    endAdornment: <InputAdornment position="end">T</InputAdornment>,
                  }}
             />

             <MuiPickersUtilsProvider utils={DateFnsUtils}>
             <KeyboardDatePicker
                disableToolbar
                required
                variant="inline"
                format="MM/dd/yyyy"
                margin="normal"
                id="date-picker-inline"
                label="Выберите дату"
                value={formState.date}
                onChange={handleDateChange}
                KeyboardButtonProps={{
                    'aria-label': 'change date',
                }}
            />
            </MuiPickersUtilsProvider>
            <FormUpload handleUploadFile={handleUploadFile}/>
              <Button variant="outlined" className={classes.button} type="submit">
                Отправить
              </Button>
              </form>
         </div>

         <TextField 
                onSubmit={submitForm}
                onError={errors => console.log(errors)}
            >
                <TextValidator
                    label="Email"
                    onChange={handleChange('adress')}
                    name="email"
                    value={formState.adress}
                    validators={['required', 'isEmail']}
                    errorMessages={['this field is required', 'email is not valid']}
                />
                <Button type="submit">Submit</Button>
            </TextField>
         </CardContent>
         </Card>
    );
}

export default DeliveryForm;
