import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useDispatch, useSelector} from 'react-redux';
import { sendFormData } from '../../../actions/actions';

import FormSelect from './delivery__form-select';
import FormUpload from './delivery__form-upload';
import { schema, convert, schemaValidate } from '../../../utils/VALIDATIONS';

import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import InputAdornment from '@material-ui/core/InputAdornment';
import blue from "@material-ui/core/colors/blue";
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import NumberFormat from 'react-number-format';
import PropTypes from 'prop-types';
import DateFnsUtils from '@date-io/date-fns';
import CircularProgress from '@material-ui/core/CircularProgress';

import {
    MuiPickersUtilsProvider,
    KeyboardDatePicker,
} from '@material-ui/pickers';

function NumberFormatCustom(props) {
    const { inputRef, onChange, ...other } = props;
    return (
        <NumberFormat
            {...other}
            getInputRef={inputRef}
            onValueChange={values => {
                onChange({
                    target: {
                        name: values.name,
                        value: values.value,
                    },
                });
            }}
            isNumericString
        />
    );
}

NumberFormatCustom.propTypes = {
    inputRef: PropTypes.func.isRequired,
    onChange: PropTypes.func.isRequired,
};

const useStyles = makeStyles((theme) => ({
    card: {
        minWidth: 275,
    },
    button: {
        color: blue[900],
        margin: 10
    },
    root: {
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            minWidth: 240,
            maxWidth: 450,
            width: '100%',
            textField: {
                flexBasis: 200,
                textField: {
                    marginLeft: theme.spacing(1),
                    marginRight: theme.spacing(1),
                },
            },
        },
    },
}));

function DeliveryForm(props) {

    const dispatch = useDispatch();
    const isLoading = useSelector(state => state.shared.isLoading);

    const [formState, setFormState] = useState({
        values: {
            rigion: '',
            waterbase: '',
            weight: '',
            adress: '',
            date: new Date(),
            upload: ''
        },
        errors: {}
    });

    useEffect(() => {
        setFormState(state => ({
            values: { ...state.values, rigion: props.rigionValue },
            errors: { ...state.errors }
        }));
    }, [props]);

    const handleChangeForm = async (event) => {

        const { target : { name, value } } = event;
        const errors = await schemaValidate(name, value);

        setFormState(state => ({
            values: { ...state.values, [name]: value },
            errors: { ...state.errors, ...errors }
        }));
    };

    const handleWeightChange = name =>  event => {

        if (formState.values[name] && formState.values[name].toString().charAt(0) == '.') {
            setFormState( state => ({
                values: { ...state.values, [name] : '' },
                errors: { ...state.errors }
            })
            );
            return;
        }

        (async function errors() {
            const value = event.target.value;
            const errors = await schemaValidate(name, value);

            setFormState( state => ({
                values: { ...state.values, [name] : value },
                errors: { ...state.errors, ...errors }
            })
            );
        }());

    };

    const handleDateChange = name => value => {

        (async function errors() {
            const errors = await schemaValidate(name, value);

            setFormState( state => ({
                values: { ...state.values, [name] : value },
                errors: { ...state.errors, ...errors }
            })
            );
        }());

    };

    const handleSelectChange = (message, value) => {
        setFormState({
            values: { ...formState.values, waterbase : value },
            errors: { ...formState.errors, waterbase: message }
        });
    };


    const handleUploadFile = value => {
        setFormState({
            values: { ...formState.values, upload : value },
            errors: { ...formState.errors }
        });
    };

    const handleSubmit = (callback) => {
        return async function (event) {
            event.preventDefault();

            let errors = await schema.validate(formState.values, { abortEarly : false })
                .then(_ => ({})) // сбрасывает объект ошибок состояния, если ошибок нет
                .catch(convert);


            setFormState(state => ({
                values: { ...state.values },
                errors
            }));

            const isError = () => Object.entries(errors);

            if (!isError().length) {
                callback(formState.values);
            }
        };
    };

    const sendForm = (form ) => {
        dispatch(sendFormData(form));
    };

    const getError = (state, prop) => {
        if (state.errors[prop]) return true;
        return false;
    };

    const classes = useStyles();

    return (
        <Card className={classes.card}>
            <CardContent> {
                !props.rigionValue ? <CircularProgress />  :
                <div className="form-wrapper">
                <form className={classes.root} autoComplete="off" onSubmit={handleSubmit(form => {
                    sendForm(form);
                })}>
                    {props.rigionValue ? <TextField 
                        required 
                        id="standard-required1" 
                        label="Ваш регион"
                        className={classes.textField}
                        value={props.rigionValue}
                        InputProps={{
                            readOnly: true,
                        }}
                    />
                        : <CircularProgress />
                    }
                    <FormSelect handleSelectChange={handleSelectChange} name={formState.waterbase}/>
                    {formState.errors && formState.errors.waterbase &&
        <div>
            <p className="waterbase-error">Поле водобазы необходимо выбрать</p>
        </div>
                    }
                    <TextField 
                        name="weight"
                        id="standard-required2"
                        label="Напишите массу воды в тоннах"
                        className={classes.textField}
                        value={formState.values.weight}
                        onChange={handleWeightChange('weight')}
                        helperText={formState.errors.weight ? formState.errors.weight : ""}
                        error={ getError(formState, 'weight')}
                        InputProps={{
                            inputComponent: NumberFormatCustom,
                            endAdornment: <InputAdornment position="end">T</InputAdornment>,
                        }}
                    />
                    <TextField
                        name="adress"
                        label="Напишите адресс"
                        value={formState.values.adress}
                        onChange={handleChangeForm}
                        fullWidth
                        error={ getError(formState, 'adress')}
                        helperText={formState.errors.adress ? formState.errors.adress : ""}
                    />
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                        <KeyboardDatePicker
                            disableToolbar
                            variant="inline"
                            format="MM/dd/yyyy"
                            margin="normal"
                            id="date-picker-inline"
                            label="Выберите дату"
                            value={formState.values.date}
                            onChange={handleDateChange('date')}
                            helperText={formState.errors.date ? formState.errors.date : ""} 
                            error={ getError(formState, 'date')}
                            KeyboardButtonProps={{
                                'aria-label': 'change date',
                            }}
                        />
                    </MuiPickersUtilsProvider>
                    <FormUpload handleUploadFile={handleUploadFile}/>
                    <Button variant="outlined" className={classes.button} type="submit">
            Отправить
                    </Button>
                </form>
            </div>
                }

            </CardContent>
        </Card>
    );
}

DeliveryForm.propTypes = {
    rigionValue: PropTypes.string,
};

export default DeliveryForm;
