import React, { Component } from "react";
import { Formik } from "formik";
import withStyles from "@material-ui/core/styles/withStyles";
import DeliveryForm from "../delivery__modules/delivery_form";
import Paper from "@material-ui/core/Paper";
import * as Yup from "yup"

const validationSchema = Yup.object({
    rigion: Yup.string("Enter a name")
    .required("Name is required"),
    weight: Yup.string("Введите ваш вес")
    .required("Вес обязателен для заполнения"),
    adress: Yup.string("")
    .min(8, "address must contain at least 8 characters")
    .required("Enter your address"),
    confirmPassword: Yup.string("Enter your password")
    .required("Confirm your password")
    .oneOf([Yup.ref("password")], "Password does not match")
})

const styles = theme => ({
 paper: {
   marginTop: theme.spacing.unit * 8,
   display: "flex",
   flexDirection: "column",
   alignItems: "center",
   padding: `${theme.spacing.unit * 5}px ${theme.spacing.unit * 5}px ${theme
     .spacing.unit * 5}px`
 },
 container: {
   maxWidth: "200px"
 }
});

function InputForm(props) {

   const classes = props;
   const values = { rigion: "", weight: "",  adress: "", password: "" };

   const submit = data => {
    console.log(data);
  };
   return (
     <React.Fragment>
          <div className={classes.container}>
         <Paper elevation={1} className={classes.paper}>
           <h1>Form</h1>
           <Formik
             render={props => <DeliveryForm {...props} />}
             initialValues={values}
             validationSchema={validationSchema}
             onSubmit={submit}
           />
         </Paper>
       </div>
     </React.Fragment>
   );
}

export default withStyles(styles)(InputForm);
