import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import FormHelperText from '@material-ui/core/FormHelperText'
import CircularProgress from '@material-ui/core/CircularProgress';
import { useSelector } from 'react-redux';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 240,
    maxWidth: 450,
    width: '100%', 
    // margin: 0,
    fullWidth: true,
    // display: "flex",
    // wrap: "nowrap"

  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

export default function FormSelect(props) {
  const classes = useStyles();

  const waterbases = useSelector(state => state.waterBases.wBfilterList)


  const handleChange = (event) => {

    props.handleSelectChange(event.target.value);

  };

  return (
    <div> {
        waterbases.length ?
        <FormControl className={classes.formControl}>
           <InputLabel htmlFor="age-native-simple">Выберите водобазу</InputLabel>
                <Select
                native
                onChange={handleChange}
                label={props.name}
                helperText={!props.name ? 'Необходимо выбрать водобазу' : ""} 
                error={!props.name}
                inputProps={{
                    name: props.name,
                    id: 'age-native-simple',
                }}
                >
                <option value=""/>
                { waterbases.map(item => <option key={item.uuid } {...item} value={item.name}>{item.name}</option>) }
                </Select>
                <FormHelperText>Error</FormHelperText>
      </FormControl>
        :
        <CircularProgress />
        }

    </div>
  );
}
